frontApp.controller("categoryPostController",["$scope","$http","$window","CategoryPost",function(n,t,i,r){n.categoryPosts=[];n.posts=[];n.idCategory=angular.element("#idCategory").val();n.idCategory=2;n.tinMois=[];n.tinNoiBats=[];t.get("/API/CategoriesAPI/").success(function(t){var i=r.getallCategory(t);angular.forEach(i,function(t){t.idCategoryParent=="1"&&n.categoryPosts.push(t)})});t.get("/API/PostsAPI?att=idCategory&&value="+n.idCategory).success(function(t){n.posts=t});t.get("/API/PostsAPI?att=idCategoryMoi&&value=2").success(function(t){n.tinMois=t});t.get("/API/PostsAPI?att=feature&&value=1").success(function(t){n.tinNoiBats=t})}]);